// Re-implementation of the internal/is package generated by declare.py
// If the condition is not met then t.Fatalf is called with an
// AssertFailed message. Returns false if the test passed, otherwise true.
package assert

import (
	"reflect"
	"testing"
	"time"

	"github.com/quiteclose/goatly/internal/is"
	//"github.com/quiteclose/goatly/pkg/run"
)

///////////////////////////////////////////////////////////////////////////////

// Any call t.Fatalf unless any item from sub-set b is in a
func Any(t *testing.T, a, b interface{}, message string) bool {
	conditionMet, reason := is.Any(a, b)
	if !conditionMet {
		t.Fatalf("AssertFailed: %s\n%s", message, reason)
	}
	return conditionMet
}

